import { useSelector } from "react-redux";
import Badge from 'react-bootstrap/Badge';
import { useNavigate } from "react-router-dom";

const FilteredMovieList = ({item}) => {
    let navigate = useNavigate();
    const {withGenres} = useSelector(state=>state.filter);
    // URL 값 중에 null값이 있어 해당 코드 수정
    let imageUrl = item.poster_path ? `https://www.themoviedb.org/t/p/w710_and_h400_multi_faces/${item.poster_path}` : 'data:image/png;base64,';
    console.log(item)

    return ( 
        <div onClick={()=>navigate(`${item.id}`)}
        className="card"
        style={{backgroundImage:
        'url('+`${imageUrl}`+')', 
        width:300, height:500,
        backgroundPosition: "center",
        backgroundRepeat: "no-repeat",
        backgroundSize: "cover",
        cursor : "painter",
        borderRadius : "10px",
        }}>


        <div className='overlay'>
            <h1 className="card_title">{item.title}</h1>
            <div>{item.genre_ids.map(id=><Badge bg="danger">
                {// 장르별 id와 item의 장르 id와 같은 값을 찾음
                withGenres && withGenres.find((item) => item.id === id).name }
            </Badge>)}

            </div>

            <div className="movie_font">
                {item.overview}
            </div>

            <div>
                <span>평점 : {item.vote_average}</span> <br/>
                <span>연령제한 : {item.adult ? "청불" : "Under 18"}</span>
            </div>
        </div>

        </div>
    );
}

export default FilteredMovieList;
